import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './DungeonBreakdown.css';
import teamMembers from '../teamMembers';

const DungeonBreakdown = () => {
    const [dungeonData, setDungeonData] = useState([]);


    useEffect(() => {
     const fetchDungeonData = async () => {
        const fetchedDungeonData = [];

         for (const defaultCharacter of teamMembers) {
            const result = await axios(
              `https:raider.io/api/v1/characters/profile?region=us&realm=${defaultCharacter.server}&name=${defaultCharacter.name}&fields=mythic_plus_best_runs%3Aall`
            );
            console.log("Dungeon Data: ", result);
            fetchedDungeonData.push(result.data);
        }
        setDungeonData(fetchedDungeonData);
      };
        fetchDungeonData();
      }, []);

      function formatTime(ms){
        const minutes = Math.floor(ms / 60000);
        const seconds = Math.floor(ms % 60000 / 1000);
        const miliseconds = ms % 1000;

        return `${minutes}m ${seconds}s ${miliseconds}ms`;
      }

      const dungeonAbbreviations = {
        //Season 1 DF
        "Shadowmoon Burial Grounds": 'SBG',
        "Court of Stars": 'COS',
        "Temple of the Jade Serpent": 'TJS',
        "Halls of Valor":'HOV',
        "Ruby Life Pools":'RLP',
        "The Azure Vault":'AV',
        "The Nokhud Offensive":'NO',
        "Algeth\'ar Academy":'AA'
      };

      return (
        <div>
          <h2>Dungeon Breakdown</h2>
          {dungeonData.map((characterDungeons, index) => (
            <div key={index}>
              <h3>{characters[index].name}</h3>
                <ul>
                  {characterDungeons.mythic_plus_best_runs.map((run) => (
                    <li key={run.dungeon.id}>
                      {dungeonAbbreviations[run.dungeon.name] || run.dungeon.name}: {formatTime(run.clear_time_ms)}
                    </li>
                  ))}
    </ul>
  </div>
))}

        </div>
      );


};

export default DungeonBreakdown;
