import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './App.css';
import Navbar from './components/Navbar';
import Home from './components/Home';
import Member from './components/Member';


function App() {

  const [character, setCharacter] = useState(null);
  const [searchText, setSearchText] = useState('');
  const [searchCategory, setSearchCategory] = useState('Category1');

  const searchCharacter = async (query) => {
    const result = await axios(
      `https://raider.io/api/v1/characters/profile?region=us&realm=${searchCategory}&name=${searchText}`
    );
    setCharacter(result.data);
  };

 useEffect(() => {
    const fetchDefaultCharacters = async () => {
      const fetchedCharacters = [];

      for (const defaultCharacter of defaultCharacters) {
        const result = await axios(
          `https://raider.io/api/v1/characters/profile?region=us&realm=${defaultCharacter.server}&name=${defaultCharacter.name}`
        );
        fetchedCharacters.push(result.data);
      }

      setCharacters(fetchedCharacters);
    };

    fetchDefaultCharacters();
  }, []);

  return (
    <div className="App">
      <Navbar onCharacterSearch={setCharacter}/>
      <div className="content">
        <Member character={character}/>
        <Home />
        {/* Your other components and content */}
      </div>
    </div>
  );
}

export default App;
